<style lang="scss">
@import "../styles/bootstrap.scss";

:root {
  --nav-h: 100px;

  @include media-breakpoint-down(md) {
    --nav-h: 50px;
  }
}

.cookbook-navbar {
  height: var(--nav-h);

  .logo {
    vertical-align: middle;

    @include media-breakpoint-down(md) {
      max-width: 200px;
    }
  }

  .cookbook-navbar-items {
    display: flex;

    align-items: center;
    justify-content: flex-end;
  }

  &.fixed {
    z-index: 11;

    position: sticky;
    top: 0;
    left: 0;
    right: 0;

    border-bottom: 1px solid var(--c-bg);
    background-color: rgba(var(--c-bg-rgb), 0.6);
    backdrop-filter: blur(16px);
  }

  .search-box {
    --search-input-width: 10rem;
    margin-left: 2.5rem;

    @include media-breakpoint-down(md) {
      --search-input-width: 100%;
      margin-left: 1.5rem;
    }

    input {
      border-radius: 8px;

      background-position-y: 0.4rem;
    }

    .suggestions {
      z-index: 100;
      display: block;
    }

    .suggestion {
      a {
        color: var(--c-text-lighter);
      }

      &.focus {
        a {
          color: var(--c-brand);
        }
      }
    }
  }

  .hidden-on-search {
    opacity: 1;
    transition: opacity 0.18s;
  }

  &.search-expanded {
    @include media-breakpoint-down(md) {
      .hidden-on-search {
        opacity: 0;
      }
    }
  }
}
</style>
